img = Image.open(BytesIO(base64.b64decode(image_byte_filtered)))
img = img.convert('RGB')
img = img.resize((224, 224))
img = image.img_to_array(img) / 255

#img = image.img_to_array(image.load_img(Image.open(image_read_io), target_size=(224, 224))) / 255.
img = img.astype('float16')
payload = {
  'instances': [img.tolist()]
}
r = requests.post('http://localhost:10000/v1/models/ImageClassifier:predict', json=payload)
pred = json.loads(r.content.decode('utf-8'))
pred_res = json.dumps(inception_v3.decode_predictions(np.array(pred['predictions']))[0])

